import React from 'react';
import {Card, CardActions, CardActionArea, CardContent, CardMedia, Button, Typography} from '@material-ui/core';

//this is the file that will show the data for a specific article

import useStyles from './styles.js';

const NewsCard = ({article: {description, publishedAt, source, title, url, urlToImage}, i}) => {

    const classes = useStyles();


    return (
        <Card className={classes.card}>
            <CardActionArea href={url} target="_blank">
                <CardMedia className = {classes.media} image={urlToImage || 'https://as2.ftcdn.net/jpg/03/29/24/37/500_F_329243708_oXkPklGDS1BMjtwrWrxepInCGbjag0DL.jpg'} />
                <div className = {classes.details}>
                    <Typography variant="body2" color="textSecondary" component = "h2">{(new Date(publishedAt)).toDateString()}</Typography>
                    <Typography variant="body2" color="textSecondary" component = "h2">{source.name}</Typography>
                </div>
                <Typography className = {classes.title} gutterBottom variant="h5">{title}</Typography>
                <CardContent>
                    <Typography variant="body2" color="textSecondary" component = "p">{description}</Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button size="small" color="primary"> Learn More</Button>
                <Typography variant="h5" color="textSecondary">{i + 1}</Typography>
            </CardActions>
        </Card>
    )
}

export default NewsCard;
